tinymce.PluginManager.add("smp",function(t){
	t.addButton("smp",{text:"Insert phrase",icon:!1,onclick:function(){
		t.windowManager.open({
			title:"Example plugin",
			body:[{type:"textbox",name:"title",label:"Title"}],
			onsubmit:function(e){
				t.insertContent("Title: "+e.data.title)
			}
		})
	}}),
	t.addMenuItem("smp",{
		text:"Controlled Phrase",
		context:"insert",
		onclick:function(){
			t.windowManager.open({
				title:"Insert Controlled Terms",
				url:"http://sliced.ro/smp/use_term.php",
				width:500,
				height:500,
				buttons:[
				{text:"Close",onclick:"close"},
				{text:"OK",
				onclick: function() {	
						t.execCommand('mceInsertContent', false, window.frames[1].get_update_text());
						this.parent().parent().close()
					}
				},

				]
			})
		}
	})
});
